import React, { useState, useRef } from 'react';
import Webcam from 'react-webcam';
import { Camera, Upload, Video, Image as ImageIcon, Loader, AlertCircle, CheckCircle, XCircle } from 'lucide-react';
import axios from 'axios';
import './App.css';

const API_URL = 'http://localhost:5000/api';

function App() {
  // State
  const [mode, setMode] = useState('camera'); // camera, image, video
  const [isProcessing, setIsProcessing] = useState(false);
  const [results, setResults] = useState(null);
  const [error, setError] = useState(null);
  const [selectedFile, setSelectedFile] = useState(null);
  const [previewUrl, setPreviewUrl] = useState(null);
  const [analysisType, setAnalysisType] = useState('all'); // all, emotion, fingers, objects

  const webcamRef = useRef(null);
  const fileInputRef = useRef(null);

  // Capture image from webcam
  const captureImage = () => {
    const imageSrc = webcamRef.current.getScreenshot();
    return imageSrc;
  };

  // Handle file selection
  const handleFileSelect = (event) => {
    const file = event.target.files[0];
    if (file) {
      setSelectedFile(file);
      setPreviewUrl(URL.createObjectURL(file));
      setResults(null);
      setError(null);
    }
  };

  // Analyze image
  const analyzeImage = async () => {
    try {
      setIsProcessing(true);
      setError(null);
      setResults(null);

      let imageData;

      // Get image based on mode
      if (mode === 'camera') {
        imageData = captureImage();
        if (!imageData) {
          throw new Error('Failed to capture image from camera');
        }
      } else if (mode === 'image' && selectedFile) {
        imageData = selectedFile;
      } else {
        throw new Error('No image available');
      }

      // Prepare request
      let endpoint = `${API_URL}/analyze-all`;
      if (analysisType === 'emotion') endpoint = `${API_URL}/detect-emotion`;
      else if (analysisType === 'fingers') endpoint = `${API_URL}/count-fingers`;
      else if (analysisType === 'objects') endpoint = `${API_URL}/count-objects`;

      let response;

      if (typeof imageData === 'string') {
        // Base64 image from webcam
        response = await axios.post(endpoint, {
          image: imageData,
          annotate: true
        });
      } else {
        // File upload
        const formData = new FormData();
        formData.append('file', imageData);
        response = await axios.post(`${endpoint}?annotate=true`, formData, {
          headers: { 'Content-Type': 'multipart/form-data' }
        });
      }

      setResults(response.data);
      setIsProcessing(false);
    } catch (err) {
      console.error('Analysis error:', err);
      setError(err.response?.data?.error || err.message || 'Analysis failed');
      setIsProcessing(false);
    }
  };

  // Clear results
  const clearResults = () => {
    setResults(null);
    setError(null);
    setSelectedFile(null);
    setPreviewUrl(null);
  };

  // Render mode selector
  const renderModeSelector = () => (
    <div className="mode-selector">
      <button
        className={`mode-btn ${mode === 'camera' ? 'active' : ''}`}
        onClick={() => { setMode('camera'); clearResults(); }}
      >
        <Camera size={24} />
        <span>Live Camera</span>
      </button>
      <button
        className={`mode-btn ${mode === 'image' ? 'active' : ''}`}
        onClick={() => { setMode('image'); clearResults(); }}
      >
        <ImageIcon size={24} />
        <span>Upload Image</span>
      </button>
      <button
        className={`mode-btn ${mode === 'video' ? 'active' : ''}`}
        onClick={() => { setMode('video'); clearResults(); }}
      >
        <Video size={24} />
        <span>Upload Video</span>
      </button>
    </div>
  );

  // Render analysis type selector
  const renderAnalysisSelector = () => (
    <div className="analysis-selector">
      <label>Analysis Type:</label>
      <select value={analysisType} onChange={(e) => setAnalysisType(e.target.value)}>
        <option value="all">All (Emotion + Fingers + Objects)</option>
        <option value="emotion">Emotion Detection Only</option>
        <option value="fingers">Finger Counting Only</option>
        <option value="objects">Object Counting Only</option>
      </select>
    </div>
  );

  // Render camera view
  const renderCamera = () => (
    <div className="camera-container">
      <Webcam
        ref={webcamRef}
        screenshotFormat="image/jpeg"
        className="webcam"
        videoConstraints={{
          facingMode: 'user',
          width: 1280,
          height: 720
        }}
      />
    </div>
  );

  // Render file upload
  const renderFileUpload = () => (
    <div className="upload-container">
      {previewUrl ? (
        <div className="preview">
          {mode === 'image' ? (
            <img src={previewUrl} alt="Preview" />
          ) : (
            <video src={previewUrl} controls />
          )}
        </div>
      ) : (
        <div className="upload-placeholder" onClick={() => fileInputRef.current?.click()}>
          <Upload size={64} />
          <p>Click to upload {mode === 'image' ? 'an image' : 'a video'}</p>
          <span>Supported: JPG, PNG, {mode === 'video' ? 'MP4, AVI, MOV' : 'GIF'}</span>
        </div>
      )}
      <input
        ref={fileInputRef}
        type="file"
        accept={mode === 'image' ? 'image/*' : 'video/*'}
        onChange={handleFileSelect}
        style={{ display: 'none' }}
      />
    </div>
  );

  // Render results
  const renderResults = () => {
    if (!results) return null;

    return (
      <div className="results-container">
        <h2>Analysis Results</h2>

        {/* Annotated Image */}
        {results.annotated_image && (
          <div className="annotated-image">
            <img src={results.annotated_image} alt="Annotated" />
          </div>
        )}

        {/* Emotion Results */}
        {(results.emotion || results.faces_detected !== undefined) && (
          <div className="result-section emotion-section">
            <h3>üòä Emotion Detection</h3>
            {results.emotion?.faces_detected > 0 || results.faces_detected > 0 ? (
              <div>
                <p className="faces-count">
                  <CheckCircle size={20} color="green" />
                  {results.emotion?.faces_detected || results.faces_detected} face(s) detected
                </p>
                {(results.emotion?.emotions || results.emotions || []).map((emotion, idx) => (
                  <div key={idx} className="emotion-card">
                    <div className="emotion-header">
                      <span className="emotion-label">{emotion.emotion}</span>
                      <span className="confidence">{emotion.confidence}% confident</span>
                    </div>
                    {emotion.quote && (
                      <div className="quote">
                        <p>"{emotion.quote}"</p>
                      </div>
                    )}
                  </div>
                ))}
              </div>
            ) : (
              <p className="no-detection">
                <XCircle size={20} color="orange" />
                No faces detected
              </p>
            )}
          </div>
        )}

        {/* Finger Results */}
        {results.fingers && (
          <div className="result-section fingers-section">
            <h3>üñêÔ∏è Finger Counting</h3>
            {results.fingers.hands_detected > 0 ? (
              <div>
                <p className="finger-count">
                  <CheckCircle size={20} color="green" />
                  Total Fingers: <strong>{results.fingers.total_fingers}</strong>
                </p>
                <p className="message">{results.fingers.message}</p>
                {results.fingers.hands.map((hand, idx) => (
                  <div key={idx} className="hand-card">
                    <p><strong>{hand.hand} Hand:</strong> {hand.fingers_up} fingers</p>
                    <p>Raised: {hand.raised_fingers.join(', ')}</p>
                  </div>
                ))}
              </div>
            ) : (
              <p className="no-detection">
                <XCircle size={20} color="orange" />
                No hands detected
              </p>
            )}
          </div>
        )}

        {/* Object Results */}
        {results.objects && (
          <div className="result-section objects-section">
            <h3>üîç Object Counting</h3>
            <p className="object-count">
              <CheckCircle size={20} color="green" />
              Objects Found: <strong>{results.objects.count}</strong>
            </p>
            <p className="message">{results.objects.message}</p>
            <p className="method">Method: {results.objects.method}</p>
          </div>
        )}
      </div>
    );
  };

  return (
    <div className="App">
      {/* Header */}
      <header className="header">
        <h1>ü§ñ AI Vision App</h1>
        <p>Facial Expression Recognition & Object Counter</p>
      </header>

      {/* Main Content */}
      <main className="main-content">
        {/* Mode Selector */}
        {renderModeSelector()}

        {/* Analysis Type Selector */}
        {renderAnalysisSelector()}

        {/* Input Area */}
        <div className="input-area">
          {mode === 'camera' ? renderCamera() : renderFileUpload()}
        </div>

        {/* Action Buttons */}
        <div className="action-buttons">
          <button
            className="analyze-btn"
            onClick={analyzeImage}
            disabled={isProcessing || (mode !== 'camera' && !selectedFile)}
          >
            {isProcessing ? (
              <>
                <Loader className="spin" size={20} />
                <span>Analyzing...</span>
              </>
            ) : (
              <>
                <Camera size={20} />
                <span>Analyze</span>
              </>
            )}
          </button>
          {(results || error) && (
            <button className="clear-btn" onClick={clearResults}>
              Clear
            </button>
          )}
        </div>

        {/* Error Message */}
        {error && (
          <div className="error-message">
            <AlertCircle size={20} />
            <span>{error}</span>
          </div>
        )}

        {/* Results */}
        {renderResults()}
      </main>

      {/* Footer */}
      <footer className="footer">
        <p>Made with ‚ù§Ô∏è using React + Flask + TensorFlow</p>
        <p>By Isparsh Tiwari</p>
      </footer>
    </div>
  );
}

export default App;
