import React, { useState, useRef } from 'react';
import Webcam from 'react-webcam';
import axios from 'axios';
import './App.css';

const API_URL = 'http://localhost:5000/api';

// â”€â”€ Icons â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€
const Ic = ({ d, size = 18, vb = "0 0 24 24" }) => (
  <svg width={size} height={size} viewBox={vb} fill="none" stroke="currentColor" strokeWidth="1.5">
    {d.map((p, i) => <path key={i} d={p} />)}
  </svg>
);
const CameraIcon  = () => <Ic d={["M23 19a2 2 0 0 1-2 2H3a2 2 0 0 1-2-2V8a2 2 0 0 1 2-2h4l2-3h6l2 3h4a2 2 0 0 1 2 2z","M12 9a4 4 0 1 0 0 8 4 4 0 0 0 0-8z"]} />;
const UploadIcon  = () => <Ic d={["M21 15v4a2 2 0 0 1-2 2H5a2 2 0 0 1-2-2v-4","M17 8l-5-5-5 5","M12 3v12"]} />;
const VideoIcon   = () => <Ic d={["M23 7l-7 5 7 5V7z","M1 5h15a2 2 0 0 1 2 2v10a2 2 0 0 1-2 2H1a2 2 0 0 1-2-2V7a2 2 0 0 1 2-2z"]} />;
const ScanIcon    = () => <Ic d={["M3 7V5a2 2 0 0 1 2-2h2","M17 3h2a2 2 0 0 1 2 2v2","M21 17v2a2 2 0 0 1-2 2h-2","M7 21H5a2 2 0 0 1-2-2v-2","M7 7h10v10H7z"]} />;
const AlertIcon   = () => <Ic d={["M10.29 3.86L1.82 18a2 2 0 0 0 1.71 3h16.94a2 2 0 0 0 1.71-3L13.71 3.86a2 2 0 0 0-3.42 0z","M12 9v4","M12 17h.01"]} />;
const SpinnerIcon = () => (
  <svg width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" className="spin">
    <path d="M21 12a9 9 0 1 1-6.219-8.56"/>
  </svg>
);
const XIcon = () => (
  <svg width="14" height="14" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
    <line x1="18" y1="6" x2="6" y2="18"/><line x1="6" y1="6" x2="18" y2="18"/>
  </svg>
);

const EMOTION_EMOJI = { happy:'ðŸ˜Š', sad:'ðŸ˜¢', angry:'ðŸ˜ ', surprise:'ðŸ˜®', neutral:'ðŸ˜', fear:'ðŸ˜¨', disgust:'ðŸ¤¢' };

// â”€â”€ Feature registry â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€
const FEATURES = [
  { id:'all',        label:'Full Analysis',        emoji:'ðŸŽ¯', endpoint:'/analyze-all',       desc:'All original features combined' },
  { id:'emotion',    label:'Emotion Detection',     emoji:'ðŸ˜Š', endpoint:'/detect-emotion',    desc:'7 facial expressions' },
  { id:'fingers',    label:'Finger Counter',        emoji:'ðŸ–ï¸', endpoint:'/count-fingers',     desc:'Both hands tracked' },
  { id:'objects',    label:'Object Counter',        emoji:'ðŸ”', endpoint:'/count-objects',     desc:'Contour based detection' },
  { id:'eye-blink',  label:'Drowsiness Detection',  emoji:'ðŸ˜´', endpoint:'/eye-blink',         desc:'EAR blink analysis' },
  { id:'gesture',    label:'Gesture Recognition',   emoji:'ðŸ‘', endpoint:'/gesture',           desc:'12+ hand gestures' },
  { id:'bg-remove',  label:'Background Removal',    emoji:'ðŸŽ¨', endpoint:'/background-remove', desc:'Selfie segmentation' },
  { id:'face-mesh',  label:'Face Mesh',             emoji:'ðŸ“Š', endpoint:'/face-mesh',         desc:'478 facial landmarks' },
  { id:'age-gender', label:'Age & Gender',          emoji:'ðŸ‘¤', endpoint:'/age-gender',        desc:'AI-powered estimation' },
  { id:'pose',       label:'Pose Detection',        emoji:'ðŸ’ª', endpoint:'/pose',              desc:'33 body landmarks' },
  { id:'gaze',       label:'Gaze Tracking',         emoji:'ðŸ‘ï¸', endpoint:'/gaze',              desc:'Iris position tracking' },
  { id:'ocr',        label:'Text Recognition',      emoji:'ðŸ–Šï¸', endpoint:'/ocr',              desc:'EasyOCR powered' },
  { id:'yolo',       label:'YOLO Object Detection', emoji:'ðŸš—', endpoint:'/detect-objects',    desc:'80+ object classes' },
  { id:'face-recog', label:'Face Recognition',      emoji:'ðŸ”', endpoint:'/face-recognize',    desc:'LBPH face ID' },
  { id:'qr',         label:'QR & Barcode Scanner',  emoji:'ðŸ“±', endpoint:'/qr-scan',           desc:'Auto decode' },
];

const BG_OPTIONS = ['blur','black','white','green','blue','dark'];

// â”€â”€ Main App â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€
export default function App() {
  const [page, setPage]             = useState('app');   // 'app' | 'about'
  const [mode, setMode]             = useState('camera');
  const [feature, setFeature]       = useState('all');
  const [isProcessing, setIsProc]   = useState(false);
  const [results, setResults]       = useState(null);
  const [error, setError]           = useState(null);
  const [file, setFile]             = useState(null);
  const [preview, setPreview]       = useState(null);
  const [bgOption, setBgOption]     = useState('blur');
  const [regName, setRegName]       = useState('');
  const [showReg, setShowReg]       = useState(false);

  const webcamRef = useRef(null);
  const fileRef   = useRef(null);

  const clear = () => { setResults(null); setError(null); setFile(null); setPreview(null); };

  const handleFile = e => {
    const f = e.target.files[0];
    if (!f) return;
    setFile(f); setPreview(URL.createObjectURL(f));
    setResults(null); setError(null);
  };

  const getEndpoint = () => {
    const f = FEATURES.find(f => f.id === feature);
    return f ? `${API_URL}${f.endpoint}` : `${API_URL}/analyze-all`;
  };

  const analyze = async () => {
    try {
      setIsProc(true); setError(null); setResults(null);
      const ep = getEndpoint();
      let params = '?annotate=true';
      if (feature === 'bg-remove') params += `&background=${bgOption}`;

      let response;
      if (mode === 'camera') {
        const img = webcamRef.current?.getScreenshot();
        if (!img) throw new Error('Could not capture from camera');
        const body = { image: img, annotate: true };
        if (feature === 'bg-remove') body.background = bgOption;
        response = await axios.post(ep, body);
      } else {
        if (!file) throw new Error('No file selected');
        const fd = new FormData();
        fd.append('file', file);
        response = await axios.post(ep + params, fd, { headers: { 'Content-Type': 'multipart/form-data' } });
      }
      setResults(response.data);
    } catch (err) {
      setError(err.response?.data?.error || err.message || 'Analysis failed');
    } finally {
      setIsProc(false);
    }
  };

  const registerFace = async () => {
    if (!regName.trim()) return;
    try {
      setIsProc(true); setError(null);
      let response;
      if (mode === 'camera') {
        const img = webcamRef.current?.getScreenshot();
        response = await axios.post(`${API_URL}/face-register?name=${encodeURIComponent(regName)}`, { image: img });
      } else {
        if (!file) throw new Error('No file selected');
        const fd = new FormData(); fd.append('file', file);
        response = await axios.post(`${API_URL}/face-register?name=${encodeURIComponent(regName)}`, fd, {
          headers: { 'Content-Type': 'multipart/form-data' }
        });
      }
      setResults(response.data); setShowReg(false); setRegName('');
    } catch (err) {
      setError(err.response?.data?.error || err.message || 'Registration failed');
    } finally {
      setIsProc(false);
    }
  };

  const currentFeature = FEATURES.find(f => f.id === feature);

  // â”€â”€ RENDER â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€
  return (
    <div className="App">

      {/* HEADER */}
      <header className="header">
        <div className="header-inner">
          <div className="header-logo">
            <div className="logo-icon">ðŸ¤–</div>
            <div className="logo-text">
              <span className="logo-title">VisionAI</span>
              <span className="logo-sub">v2.0.0 Â· 15 FEATURES</span>
            </div>
          </div>
          <nav className="header-nav">
            <button className={`nav-btn ${page==='app'?'active':''}`} onClick={() => setPage('app')}>Analyzer</button>
            <button className={`nav-btn ${page==='about'?'active':''}`} onClick={() => setPage('about')}>About</button>
          </nav>
          <div className="header-status">
            <div className="status-dot" />
            SYSTEM ONLINE
          </div>
        </div>
      </header>

      {page === 'app' ? <AppPage /> : <AboutPage />}

      {/* FOOTER */}
      <footer className="footer">
        <div className="footer-inner">
          <div className="footer-left">Â© 2026 VisionAI Â· Isparsh Tiwari</div>
          <div className="footer-right">
            <div className="footer-tag">Built with <span>React</span></div>
            <div className="footer-tag">Powered by <span>TensorFlow</span></div>
            <div className="footer-tag">CV via <span>MediaPipe + YOLO</span></div>
          </div>
        </div>
      </footer>
    </div>
  );

  // â”€â”€ APP PAGE â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€
  function AppPage() {
    return (
      <main className="main-content">

        {/* Hero */}
        <div className="hero">
          <div className="hero-tag"><span>â—ˆ</span> AI VISION SYSTEM Â· 15 FEATURES</div>
          <h1>Real-time <span>Computer Vision</span><br/>Analysis Platform</h1>
          <p>15 AI-powered features â€” emotion, gestures, pose, gaze, OCR, YOLO detection and more.</p>
          <div className="hero-stats">
            <div className="stat"><span>15</span>Features</div>
            <div className="stat-div"/>
            <div className="stat"><span>478</span>Landmarks</div>
            <div className="stat-div"/>
            <div className="stat"><span>80+</span>Object Classes</div>
            <div className="stat-div"/>
            <div className="stat"><span>Real-time</span>Processing</div>
          </div>
        </div>

        {/* STEP 01 â€” Input Source */}
        <div>
          <div className="section-label">01 â€” Select Input Source</div>
          <div className="mode-selector">
            {[
              { id:'camera', icon:<CameraIcon/>, title:'Live Camera',  desc:'Real-time webcam feed' },
              { id:'image',  icon:<UploadIcon/>, title:'Upload Image', desc:'JPG, PNG, GIF supported' },
              { id:'video',  icon:<VideoIcon/>,  title:'Upload Video', desc:'MP4, AVI, MOV supported' },
            ].map(m => (
              <button key={m.id} className={`mode-btn ${mode===m.id?'active':''}`}
                onClick={() => { setMode(m.id); clear(); }}>
                <div className="mode-icon">{m.icon}</div>
                <div className="mode-info">
                  <div className="mode-title">{m.title}</div>
                  <div className="mode-desc">{m.desc}</div>
                </div>
              </button>
            ))}
          </div>
        </div>

        {/* STEP 02 â€” Feature Selection */}
        <div>
          <div className="section-label">02 â€” Select Feature</div>
          <div className="feature-grid">
            {FEATURES.map(f => (
              <button key={f.id} className={`feature-btn ${feature===f.id?'active':''}`}
                onClick={() => { setFeature(f.id); clear(); }}>
                <span className="feature-emoji">{f.emoji}</span>
                <span className="feature-label">{f.label}</span>
              </button>
            ))}
          </div>

          {/* Background option */}
          {feature === 'bg-remove' && (
            <div className="feature-option">
              <span className="option-label">BACKGROUND</span>
              <div className="bg-options">
                {BG_OPTIONS.map(b => (
                  <button key={b} className={`bg-btn ${bgOption===b?'active':''}`}
                    onClick={() => setBgOption(b)}>{b}</button>
                ))}
              </div>
            </div>
          )}

          {/* Face register option */}
          {feature === 'face-recog' && (
            <div className="feature-option">
              <button className="register-btn" onClick={() => setShowReg(!showReg)}>
                {showReg ? 'âœ• Cancel' : '+ Register New Face'}
              </button>
              {showReg && (
                <div className="register-form">
                  <input className="reg-input" placeholder="Enter person's name..."
                    value={regName} onChange={e => setRegName(e.target.value)}
                    onKeyDown={e => e.key === 'Enter' && registerFace()} />
                  <button className="analyze-btn small" onClick={registerFace}
                    disabled={isProcessing || !regName.trim()}>
                    {isProcessing ? <SpinnerIcon/> : 'Register'}
                  </button>
                </div>
              )}
            </div>
          )}
        </div>

        {/* STEP 03 â€” Input Feed */}
        <div>
          <div className="section-label">03 â€” Input Feed
            {currentFeature && <span className="section-feature-badge">{currentFeature.emoji} {currentFeature.label}</span>}
          </div>
          <div className="input-area">
            {mode === 'camera' ? (
              <div className="camera-container">
                <Webcam ref={webcamRef} screenshotFormat="image/jpeg" className="webcam"
                  videoConstraints={{ facingMode:'user', width:1280, height:720 }} />
                <div className="camera-overlay"/>
              </div>
            ) : (
              <div className="upload-container">
                {preview ? (
                  <div className="preview">
                    {mode === 'image'
                      ? <img src={preview} alt="Preview"/>
                      : <video src={preview} controls/>}
                    <button className="preview-change" onClick={() => fileRef.current?.click()}>
                      Change File
                    </button>
                  </div>
                ) : (
                  <div className="upload-placeholder" onClick={() => fileRef.current?.click()}>
                    <div className="upload-icon"><UploadIcon/></div>
                    <h3>Drop file here or click to browse</h3>
                    <p>{mode === 'image' ? 'JPG Â· PNG Â· GIF' : 'MP4 Â· AVI Â· MOV'}</p>
                  </div>
                )}
                <input ref={fileRef} type="file"
                  accept={mode === 'image' ? 'image/*' : 'video/*'}
                  onChange={handleFile} style={{ display:'none' }}/>
              </div>
            )}
          </div>
        </div>

        {/* Actions */}
        <div className="action-buttons">
          <button className="analyze-btn" onClick={analyze}
            disabled={isProcessing || (mode !== 'camera' && !file)}>
            {isProcessing
              ? <><SpinnerIcon/> Processing...</>
              : <><ScanIcon/> Run {currentFeature?.label || 'Analysis'}</>}
          </button>
          {(results || error) && (
            <button className="clear-btn" onClick={clear}><XIcon/> Clear</button>
          )}
        </div>

        {/* Error */}
        {error && <div className="error-message"><AlertIcon/> {error}</div>}

        {/* STEP 04 â€” Results */}
        {results && (
          <div className="results-container">
            <div className="section-label">04 â€” Analysis Results
              <span className="section-feature-badge">{currentFeature?.emoji} {currentFeature?.label}</span>
            </div>

            {results.annotated_image && (
              <div className="annotated-image">
                <img src={results.annotated_image} alt="Annotated"/>
              </div>
            )}

            <div className="results-grid">
              <ResultCards results={results} feature={feature}/>
            </div>
          </div>
        )}

      </main>
    );
  }
}

// â”€â”€ Result Cards â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€
function ResultCards({ results, feature }) {
  if (feature === 'all') {
    return <>
      <EmotionCard data={results.emotion}/>
      <FingerCard  data={results.fingers}/>
      <ObjectCard  data={results.objects}/>
    </>;
  }
  if (feature === 'emotion')    return <EmotionCard   data={results}/>;
  if (feature === 'fingers')    return <FingerCard    data={results}/>;
  if (feature === 'objects')    return <ObjectCard    data={results}/>;
  if (feature === 'eye-blink')  return <EyeBlinkCard  data={results}/>;
  if (feature === 'gesture')    return <GestureCard   data={results}/>;
  if (feature === 'bg-remove')  return <BgRemoveCard  data={results}/>;
  if (feature === 'face-mesh')  return <FaceMeshCard  data={results}/>;
  if (feature === 'age-gender') return <AgeGenderCard data={results}/>;
  if (feature === 'pose')       return <PoseCard      data={results}/>;
  if (feature === 'gaze')       return <GazeCard      data={results}/>;
  if (feature === 'ocr')        return <OCRCard       data={results}/>;
  if (feature === 'yolo')       return <YoloCard      data={results}/>;
  if (feature === 'face-recog') return <FaceRecogCard data={results}/>;
  if (feature === 'qr')         return <QRCard        data={results}/>;
  return <RawCard data={results}/>;
}

